import { Client } from "pg";
import { Connection, DbDetailsOptions } from "@types";
export default abstract class DbDetails<O> {
    abstract getSlowQueryLogQueriesSpans(clientOrConnectionConfig: Connection | Client, options?: DbDetailsOptions): Promise<any>;
    abstract getDbDetails(clientOrConnectionConfig: Connection | Client, options?: DbDetailsOptions): Promise<any>;
    abstract getExtendedDbDetailsData(clientOrConnectionConfig: Connection | Client, options?: DbDetailsOptions): Promise<any>;
}
